import React, { useCallback, useEffect, useMemo, useState } from 'react';
import { Container, Header, HeaderContent, Profile, Schendule, Content, Calendar, NextAppointment, Section, Appointment } from './styles';
import logoImg from '../../assets/logo.svg';
import { FiClock, FiPower } from 'react-icons/fi';
import { useAuth } from '../../Hooks/Auth';
import DayPicker, { DayModifiers } from 'react-day-picker';
import 'react-day-picker/lib/style.css'
import api from '../../services/api';
import { isToday, format } from 'date-fns';
import ptBR from 'date-fns/locale/pt-BR';


interface MonthAvailabilityItem {
  day: number;
  availability: boolean;
}

const Dashboard: React.FC = () => {
  const { SignOut, user } = useAuth();
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [currentMonth, setCurrentMonth] = useState(new Date())
  const [monthAvailability, setMonthAvailability] = useState<MonthAvailabilityItem[]>([]);

  const handleDateChange = useCallback((day: Date, modifiers: DayModifiers) => {
    if (modifiers.available) {
      setSelectedDate(day);
    }
  }, []);

  const handleMonthChange = useCallback((month: Date) => {
    setCurrentMonth(month);
  }, []);

  useEffect(() => {
    api.get(`/providers/${user.id}/month-availability`, {
      params: {
        year: currentMonth.getFullYear(),
        month: currentMonth.getMonth() + 1
      }
    }).then(response => {
      setMonthAvailability(response.data);
    })
  }, [currentMonth, user.id]);

  const disabledDays = useMemo(() => {
    const dates = monthAvailability
      .filter(monthDay => monthDay.availability === false)
      .map(monthDay => {
        const year = currentMonth.getFullYear();
        const month = currentMonth.getMonth();

        return new Date(year, month, monthDay.day);
      });

    return dates;
  }, [currentMonth, monthAvailability]);

  const selectedDateAsText = useMemo(() => {
    return format(selectedDate, "'Dia' dd 'de' MMMM", {
      locale: ptBR
    })
  }, [selectedDate]);

  const selectedWeekDay = useMemo(() => {
    return format(selectedDate, 'E', {
      locale: ptBR,
    })
  }, [selectedDate]);

  return (
    <Container>
      <Header>
        <HeaderContent>
          <img src={logoImg} alt="GoBarber" />
          <Profile>
            <img src={user.avatar_url} alt={user.name} />
            <div>
              <span>Bem Vindo</span>
              <strong>{user.name}</strong>
            </div>
          </Profile>

          <button type="button" onClick={SignOut}>
            <FiPower />
          </button>
        </HeaderContent>
      </Header>

      <Content>
        <Schendule>
          <h1>Horarios Agendados</h1>
          <p>
            <span>{isToday(selectedDate) && 'Hoje'}</span>
            <span>{selectedDateAsText}</span>
            <span>{`${selectedWeekDay}-feira`}</span>
          </p>
          <NextAppointment>
            <strong>Atendimento a Seguir</strong>
            <div>
              <img src="https://pm1.narvii.com/6598/34eaf79133f602a4accadb557ca02b933eb27352_00.jpg" alt="Raiva" />
              <strong>Hebert Ryan</strong>
              <span> <FiClock /> 08:00 </span>
            </div>
          </NextAppointment>

          <Section>
            <strong>Manha</strong>

            <Appointment>
              <span>
                <FiClock />
                08:00
              </span>
              <div>
                <img src="https://pm1.narvii.com/6598/34eaf79133f602a4accadb557ca02b933eb27352_00.jpg" alt="Raiva" />
                <strong>Hebert Ryan</strong>
                <span> <FiClock /> 08:00 </span>
              </div>
            </Appointment>

          </Section>

          <Section>
            <strong>Tarde</strong>

            <Appointment>
              <span>
                <FiClock />
                13:00
              </span>
              <div>
                <img src="https://pm1.narvii.com/6598/34eaf79133f602a4accadb557ca02b933eb27352_00.jpg" alt="Raiva" />
                <strong>Hebert Ryan</strong>
                <span> <FiClock /> 08:00 </span>
              </div>
            </Appointment>
          </Section>
        </Schendule>
        <Calendar >
          <DayPicker
            weekdaysShort={['D', 'S', 'T', 'Q', 'Q', 'S', 'S']}
            fromMonth={new Date()}
            disabledDays={[{ daysOfWeek: [0, 6] }, ...disabledDays]}
            selectedDays={selectedDate}
            modifiers={{
              available: { daysOfWeek: [1, 2, 3, 4, 5] }
            }}
            onDayClick={handleDateChange}
            onMonthChange={handleMonthChange}
            months={[
              'Janeiro',
              'Fevereiro',
              'Março',
              'Abril',
              'Maio',
              'Junho',
              'Julho',
              'Agosto',
              'Setembro',
              'Outubro',
              'Novembro',
              'Dezembro'
            ]}
          />
        </Calendar>
      </Content>
    </Container>
  );
};

export default Dashboard;
