import styled from 'styled-components';
import { shade } from 'polished';
import SignInBackground from '../../assets/sign-in-background.png';

export const Container = styled.div`
  height: 100vh;
  display: flex;
  align-items: stretch;
`;

export const Content = styled.div`
  display: flex;
  flex-direction: column;
  place-content: center;
  width: 100%;
  max-width: 700px;
  align-items: center;

  form {
    margin: 80px 0px;
    text-align: center;
    width: 340px
  }
  h1{ 
    margin-bottom: 24px;
  }
  input {
    background: #232129;
    border-radius: 10px;
    border: 2px solid #232129;
    padding: 16px;
    width: 100%;
    color: #F4EDE8;
    &::placeholder {
      color: #666360;
    }

    & + input {
      margin-top: 8px;
    }
  }
  button {
    background: #ff9000;
    height: 56px;
    border-radius: 10px;
    border: 0;
    padding: 0 16px;
    width: 100%;
    color: #312E38;
    font-weight: 500;
    margin-top: 24px;
    transition: background-color 0.2s; 

    &:hover {
      background: ${shade(0.2, '#ff9000')};
    }
  }

  a {
    display: block;
    color: #F4EDE8;
    text-decoration: none;
    transition: color 0.2s;
    margin-top: 24px;

    &:hover {
      color: ${shade(0.2, '#F4EDE8')};
    }
  }

  > a {
    display: flex;
    align-items: center;
    color: #ff9000;
    text-decoration: none;
    transition: color 0.2s;
    margin-top: 24px;

    &:hover {
      color: ${shade(0.2, '#ff9000')};
    }

    svg {
      margin-right: 16px;
    }
  }
`;

export const Background = styled.div`
  flex: 1;
  background: url(${SignInBackground}) no-repeat center;
  background-size: cover;
`;
